import { Component } from '@angular/core';

@Component({
  selector: 'app-acceuil',
  templateUrl: './acceuil.component.html',
  styleUrls: ['./acceuil.component.css']
})
export class AcceuilComponent {

  slides = [
    { image: '/assets/images/D1.jpg', text: 'Clinique Dentaire IFRASSEN La Meilleure Clinique Dentaire Au Centre De Casablanca' },
    { image: '/assets/images/D2.JPG', text: '+212649965944' },
    // Ajoutez autant de diapositives que nécessaire
  ];
  isTextVisible = false;

  toggleVisibility(cardText: string) {
    this.isTextVisible = !this.isTextVisible;
  }
  cards = [
    { header: 'Facettes Dentaires', body: 'Le blanchiment permet d’éliminer ou d’atténuer selon le cas les taches indésirables grâce à des produits reconnus aujourd’hui innofensifs pour les tissus dentaires.' },
    { header: 'Blanchiment Dentaire', body: 'Les prothèses dentaires un dispositif qui vise à reconstituer la dent abîmée ou à remplacer les dents absentes.' },
    { header: 'Card 3', body: 'Contenu de la carte 3' },
    // ... ajoutez autant de cartes que nécessaire
  ];
}
